import { useEffect, useRef, useState } from "react";
import socketIOClient from "socket.io-client";

const NEW_CHAT_MESSAGE_EVENT = "newChatMessage";
const SOCKET_SERVER_URL = "http://localhost:4000";

function useChat (roomId, userName) {
  const [state, setState] =  useState([{message: "", name: ""}])
  const [chats, setChats] = useState([]);
  const socketRef = useRef();

  useEffect(() => {
    socketRef.current = socketIOClient(SOCKET_SERVER_URL, {
      query: { roomId },
    });

    socketRef.current.on(NEW_CHAT_MESSAGE_EVENT, (chat) => {
      const incomingMessage = {
        ...chat,
        ownedByCurrentUser: chat.message.senderId === socketRef.current.id,
      };
      setChats((chats) => [...chats, incomingMessage]);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [roomId]);

  const sendMessage = (messageBody, userName) => {
    socketRef.current.emit(NEW_CHAT_MESSAGE_EVENT, {
      body: messageBody,
      senderId: socketRef.current.id,
      senderName: userName,
    });
  };

  return { chats, sendMessage };
};

export default useChat;
