import React, {useState} from "react";
import { useParams, useLocation } from "react-router-dom";
import "./ChatRoom.css";
import useChat from "../useChat";




function ChatRoom() {
    // const { roomId } = useParams();
    const location = useLocation();
    const {roomId, userName}= location.state;
     // console.log(roomId);
    // console.log(userName);
    const { messages, sendMessage } = useChat(roomId, userName);
    // console.log(messages);
    const [newMessage, setNewMessage] = useState({message: "", name: ""});


  

    const handleNewMessageChange = (event) => {
    setNewMessage(event.target.value);
    };

    const handleSendMessage = () => {
        const {message, name} = newMessage;
        sendMessage({message, name});
        setNewMessage({message: "", name: ""});
    };

    return (
    <div className="chat-room-container">
        <h1 className="room-name">Room: {roomId}</h1>
        <div className="messages-container">
        <ol className="messages-list">
            {messages && messages.map((message, i) => (
            <>
            <li
                key = {i}
                className={`message-item ${
                message.ownedByCurrentUser ? "my-message" : "received-message"
                }`}
            >  
            {message.ownedByCurrentUser ? "Me" : message.name}
            </li> 

            <li
                key={i}
                className={`message-item ${
                message.ownedByCurrentUser ? "my-message" : "received-message"
                }`}
            >
                
                {message.body}
            </li>
            </>
            ))}
        </ol>
        </div>
        <textarea
        value={{message: newMessage,  userName}}
        onChange={handleNewMessageChange}
        placeholder="Write message..."
        className="new-message-input-field"
        />
        <button onClick={handleSendMessage} className="send-message-button">
        Send
        </button>
    </div>
    );
};

export default ChatRoom;
